import React, { useRef, useState } from "react";
import {
  View,
  StyleSheet,
  Image,
  ScrollView,
  PanResponder,
  Animated,
  TouchableOpacity,
  Text,
  KeyboardAvoidingView,
  SafeAreaView,
} from "react-native";

import * as ImagePicker from "expo-image-picker";
import Input from "../components/input";
import InputEx from "../components/inputEx";

export default function Criacao_abaixoAssinado() {
  const [descricao, setDescricao] = useState("");
  const [selectedImage, setSelectedImage] = useState(null);

  const selectImage = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status === "granted") {
      const result = await ImagePicker.launchImageLibraryAsync();
      if (!result.cancelled) {
        setSelectedImage(result.uri);
      }
    }
  };

  return (
    <View style={styles.container}>
      <SafeAreaView>
        <ScrollView>
          <View style={styles.cima}>
            <Text style={{ fontSize: 42, color: "#FCE8CF" }}>ASS</Text>
          </View>
          <View style={styles.centro}>
            <View style={styles.Relate_titulo}>
              <Text style={{ fontSize: 20 }}>RELATE SUA RECLAMAÇÃO</Text>
            </View>
            <View style={styles.titulo}>
              <Input largura={300} altura={40} placeholder="titulo"></Input>
            </View>
            <View style={styles.anexarImagem}>
              <TouchableOpacity
                onPress={selectImage}
                style={[styles.procurarImagem, { marginTop: 0 }]}
              >
                {selectedImage ? (
                  <Image
                    source={{ uri: selectedImage }}
                    style={{ flex: 1, width: undefined, height: undefined }}
                    resizeMode="cover"
                  />
                ) : (
                  <View
                    style={[
                      styles.procurarImagem,
                      {
                        flex: 1,
                        width: "100",
                        alignItems: "center",
                        justifyC0ontent: "center",
                      },
                    ]}
                  >
                    <Image
                      style={{ flex: 1, width: 150, height: 150 }}
                      source={require("../image/icons/camera.png")}
                    ></Image>
                  </View>
                )}
              </TouchableOpacity>
            </View>
            <view style={styles.descrição}>
              <Input largura="80%" altura="100%" placeholder="titulo"></Input>
            </view>
            <View>

            </View>
          </View>
        </ScrollView>
      </SafeAreaView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "black",
  },
  cima: {
    flex: 1,
    backgroundColor: "#004361",
    justifyContent: "center",
    alignItems: "center",
  },
  centro: {
    flex: 8,
    backgroundColor: "#BDD2DC",
  },

  titulo: {
    alignItems: "center",
    justifyContent: "center",
    width: "100%",
    backgroundColor: "#fff",
    flex: 0.1,
  },

  Relate_titulo: {
    alignItems: "center",
    justifyContent: "center",
    flex: 0.13,
    width: "100%",
    backgroundColor: "#fff",
  },

  anexarImagem: {
    alignItems: "center",
    justifyC0ontent: "center",
    width: "100%",
    backgroundColor: "black",
    flex: 0.3,
  },
  procurarImagem: {
    flex: 1,
    width: "85%",
    backgroundColor: "#FCE8CF",
    borderRadius: 20,
  },
  descrição: {
    flex: 0.3,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyC0ontent: "center",
  },
  viewDescrição: {
    fontSize: 44,
    paddingLeft: "3%",
    paddingRight: "3%",
    paddingBottom: "4%",
    paddingTop: "2%",
    marginBottom: "1%",
    borderRadius: 7,
    alignItems: "center",
    justifyC0ontent: "center",
    height: "95%",
    backgroundColor: "#AECAFF",
    opacity: 0.5,
  },
});
